(function(){var bodyEl=document.body,content=document.querySelector(".content-wrap"),openbtn=document.getElementById("open-button"),closebtn=document.getElementById("close-button"),isOpen=false;function init(){initEvents()}function initEvents(){openbtn.addEventListener("click",toggleMenu);if(closebtn){closebtn.addEventListener("click",toggleMenu)}content.addEventListener("click",function(ev){var target=ev.target;if(isOpen&&target!==openbtn){toggleMenu()}})}function toggleMenu(){if(isOpen){bodyEl.ClassList.remove("show-menu")}else{bodyEl.ClassList.add("show-menu")}isOpen=!isOpen}init()})();